import React from 'react';

class Servicios extends React.Component {
  constructor(){
    super();
    this.state = {
      open: '',
      class: '',
      texto: '',
    };
    this.close = this.close.bind(this);
    this.open = this.open.bind(this);
    this.changeClass1 = this.changeClass1.bind(this);
    this.changeClass2 = this.changeClass2.bind(this);
    this.changeClass3 = this.changeClass3.bind(this);
  }
    changeClass1(){
      if (this.state.class != 'texto1'){
        this.setState({class: 'text1'});
        this.setState({texto: 'Teeeeexxxxxtooo 1'});
        this.open();
      }
    }

    changeClass2(){
      if (this.state.class != 'texto2'){
        this.setState({class: 'text2'});
        this.setState({texto: 'Teeeeexxxxxtooo 2'});
        this.open();
      }
    }

    changeClass3(){
      if (this.state.class != 'texto3'){
        this.setState({class: 'text3'});
        this.setState({texto: 'Teeeeexxxxxtooo 3'});
        this.open();
      }
    }

    open() {
      this.setState({open:true});
    }

    close() {
      this.setState({open:false});
    }



    render(){
      return(
        <div class='container-fluid recuadro-servicios'>
          <button onClick={this.changeClass1} >1</button>
          <button onClick={this.changeClass2} >2</button>
          <button onClick={this.changeClass3} >3</button>
          <div class={this.state.open?'caja-texto':'escondido'}>
            <div class='texto-servicios animated fadeIn'>
              {this.state.texto}
            </div>
          <button onClick={this.close}>Close</button>
          </div>

          <div class='borradme'>

          </div>
        </div>
      ); 
    } 
  }


export default Servicios;
